import React, { useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function App() {
  const [activeTab, setActiveTab] = useState("generate");
  const [ingredient, setIngredient] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [recipe, setRecipe] = useState(null);
  const [savedList, setSavedList] = useState([]);

  useEffect(() => {
    if (activeTab === "saved") {
      fetchSaved();
    }
  }, [activeTab]);

  async function fetchSaved() {
    try {
      const resp = await axios.get(`${API}/recipes`);
      setSavedList(resp.data);
    } catch (err) {
      console.error("Failed to fetch saved recipes", err);
    }
  }

  const addIngredient = () => {
    if (!ingredient.trim()) return;
    setIngredients([...ingredients, { name: ingredient }]);
    setIngredient("");
  };

  const removeIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const generate = async () => {
    try {
      const resp = await axios.post(`${API}/generate`, { ingredients });
      setRecipe(resp.data);
    } catch (err) {
      console.error("Recipe generation failed", err);
    }
  };

  const save = async () => {
    if (!recipe) return;
    try {
      await axios.post(`${API}/recipes`, {
        title: recipe.dishName,
        ingredients: recipe.ingredients,
        instructions: recipe.instructions,
      });
      setActiveTab("saved");
    } catch (err) {
      console.error("Save recipe failed", err);
    }
  };

  const deleteRecipe = async (id) => {
    try {
      await axios.delete(`${API}/recipes/${id}`);
      fetchSaved();
    } catch (err) {
      console.error("Delete recipe failed", err);
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: "sans-serif" }}>
      <h1>Recipe Generator</h1>

      {/* Tabs */}
      <div style={{ marginBottom: 20 }}>
        <button
          onClick={() => setActiveTab("generate")}
          style={{
            marginRight: 10,
            fontWeight: activeTab === "generate" ? "bold" : "normal",
          }}
        >
          Generate Recipe
        </button>
        <button
          onClick={() => setActiveTab("saved")}
          style={{
            fontWeight: activeTab === "saved" ? "bold" : "normal",
          }}
        >
          Saved Recipes
        </button>
      </div>

      {/* Generate Tab */}
      {activeTab === "generate" && (
        <>
          <div>
            <input
              value={ingredient}
              onChange={(e) => setIngredient(e.target.value)}
              placeholder="Enter ingredient"
            />
            <button onClick={addIngredient}>Add</button>
          </div>
          <ul>
            {ingredients.map((ing, i) => (
              <li key={i} style={{ display: "flex", alignItems: "center" }}>
                <span style={{ flexGrow: 1 }}>{ing.name}</span>
                <button onClick={() => removeIngredient(i)}>Delete</button>
              </li>
            ))}
          </ul>
          <button onClick={generate} disabled={!ingredients.length}>
            Generate Recipe
          </button>

          {recipe && (
            <div style={{ marginTop: 20 }}>
              <h2>{recipe.dishName}</h2>
              <h3>Ingredients</h3>
              <ul>
                {recipe.ingredients.map((ing, i) => (
                  <li key={i}>{ing.name}</li>
                ))}
              </ul>
              <h3>Instructions</h3>
              <ol>
                {recipe.instructions.map((step, i) => (
                  <li key={i}>{step}</li>
                ))}
              </ol>
              <button onClick={save}>Save Recipe</button>
            </div>
          )}
        </>
      )}

      {/* Saved Recipes Tab */}
      {activeTab === "saved" && (
        <div>
          <h2>Saved Recipes</h2>
          {savedList.length === 0 ? (
            <p>No saved recipes yet.</p>
          ) : (
            savedList.map((r) => (
              <div
                key={r.id}
                style={{
                  border: "1px solid #ccc",
                  padding: 10,
                  marginBottom: 20,
                  borderRadius: 4,
                }}
              >
                <h3>{r.dishName}</h3>
                <h4>Ingredients:</h4>
                <ul>
                  {r.ingredients.map((ing, i) => (
                    <li key={i}>
                      {ing.quantity
                        ? `${ing.quantity} of ${ing.name}`
                        : ing.name}
                    </li>
                  ))}
                </ul>
                <h4>Instructions:</h4>
                <ol>
                  {r.instructions.map((step, i) => (
                    <li key={i}>{step}</li>
                  ))}
                </ol>
                <button onClick={() => deleteRecipe(r.id)}>Delete</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}
