import React, { useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:8000";

function App() {
  const [ingredient, setIngredient] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [recipe, setRecipe] = useState(null);
  const [saved, setSaved] = useState(false);
  const [savedList, setSavedList] = useState([]);

  useEffect(() => {
    async function fetchSaved() {
      const resp = await axios.get(`${API}/recipes`);
      setSavedList(resp.data);
    }
    fetchSaved();
  }, [saved]);

  const addIngredient = () => {
    if (!ingredient.trim()) return;
    setIngredients([...ingredients, { name: ingredient }]);
    setIngredient("");
  };

  const generate = async () => {
    const resp = await axios.post(`${API}/generate`, { ingredients });
    setRecipe(resp.data);
    setSaved(false);
  };

  const save = async () => {
    if (!recipe) return;
    await axios.post(`${API}/recipes`, {
      title: recipe.dishName,
      ingredients: recipe.ingredients,
      instructions: recipe.instructions,
    });
    setSaved(true);
    setRecipe(null);
    setIngredients([]);
  };

  return (
    <div style={{ padding: 20, fontFamily: "sans-serif" }}>
      <h1>Recipe Generator</h1>

      <div>
        <input
          value={ingredient}
          onChange={e => setIngredient(e.target.value)}
          placeholder="Enter ingredient"
        />
        <button onClick={addIngredient}>Add</button>
      </div>

      <ul>
        {ingredients.map((ing, i) => (
          <li key={i}>{ing.name}</li>
        ))}
      </ul>

      <button onClick={generate} disabled={!ingredients.length}>
        Generate Recipe
      </button>

      {recipe && (
        <div style={{ marginTop: 20 }}>
          <h2>{recipe.dishName}</h2>
          <h3>Ingredients</h3>
          <ul>
            {recipe.ingredients.map((ing, i) => (
              <li key={i}>{ing.name}</li>
            ))}
          </ul>
          <h3>Instructions</h3>
          <ol>
            {recipe.instructions.map((step, i) => (
              <li key={i}>{step}</li>
            ))}
          </ol>
          <button onClick={save}>Save Recipe</button>
        </div>
      )}

      <h2>Saved Recipes</h2>
      <ul>
        {savedList.map(r => (
          <li key={r.id}>{r.title}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
